		update conf_mng set
		employee_nums = #{employee_nums}
		, title = #{title}

		
		, originalfile = #{originalfile}
		, savedfile = #{savedfile}
		
		where conf_num = #{conf_num}

update conf_topic set
subtitle = #{subtitle}
, employee_num = #{employee_num}
, process = #{process}
where subtitle_id = #{subtitle_id}

